  background-repeat: no-repeat;
  background-size: 85% 85%;
  outline: 2px solid var(--accent02);
  outline-offset:-3px;
}

.nav_icon {
  height: var(--core_tile_size);
  visibility: hidden;
  opacity:0;
  outline:none;

  color: var(--accent02);
  background-image: -webkit-gradient(linear, 45deg, from(var(--accent02)), to(var(--accent01)));
  background-image: -o-linear-gradient(45deg,var(--accent02), var(--accent01));
  background-image: linear-gradient(45deg,var(--accent02), var(--accent01));
  background-position:50% 50%;
  background-repeat: no-repeat;
  background-size: 0% 0%;
  display:-webkit-box;
  display:-ms-flexbox;
  display:flex;
  -webkit-box-pac:center;
      -ms-flex-pac:center;
          justify-content:center;
  -webkit-box-align:center;
      -ms-flex-align:center;
          align-items:center;
  -webkit-transition: all 0.25s;
  -o-transition: all 0.25s;
  transition: all 0.25s;

}
.nav_icon--revealed {
  visibility: visible;
  opacity: 1;
  color: var(--bright01);

  background-image: -webkit-gradient(linear, 45deg, from(var(--dark02)), to(var(--dark01)));
  background-image: -o-linear-gradient(45deg, var(--dark02), var(--dark01));
  background-image: linear-gradient(45deg, var(--dark02), var(--dark01));
  background-repeat: no-repeat;
  background-position: 50% 50%;
  background-size: 85% 85%;
}
.nav_icon--revealed:hover{
  color:var(--bright01);
  background-image: -webkit-gradient(linear,45deg, from(var(--accent02)), to(var(--accent01)));
  background-image: -o-linear-gradient(45deg,var(--accent02), var(--accent01));
  background-image: linear-gradient(45deg,var(--accent02), var(--accent01));
  background-position:50% 50%;
  background-repeat: no-repeat;
  background-size: 85% 85%;

}

.nav_icon--revealed:focus {
  visibility: visible;
  opacity: 1;
  color: var(--bright01);
  background-image: -webkit-gradient(linear,45deg,from(var(--accent02)), to(var(--accent01)));
  background-image: -o-linear-gradient(45deg,var(--accent02), var(--accent01));
  background-image: linear-gradient(45deg,var(--accent02), var(--accent01));
  background-position:50% 50%;
  background-repeat: no-repeat;
  background-size: 66% 66%;
  border-radius:0.5rem;
  outline: 2px solid var(--accent01);
  outline-offset: -8px;
  -webkit-transition: none;
  -o-transition: none;
  transition: none;
}
