/*
* Prefixed by https://autoprefixer.github.io
* PostCSS: v7.0.29,
* Autoprefixer: v9.7.6
* Browsers: last 4 version
*/


:root {
  font-size:16px;
  --dark01: #171216;
  --dark02: #473F45;
  --dark03: #888888;
  --dark_transparent: rgba(0,0,0,0.07);
  --bright01: #fafafa;
  --bright02: #eeeeee;
  --bright03:#EDEBFD;
  --bright04:#D9D8DE;
  --action: #DE6B6B;
  --accent01: var(--apuxie)/*#F97171*/ ;
  --accent02: var(--purple)/*#675C79*/;
  --accent03: #6BE1F1;
  --heroBG_color:#E7EDF1;
  --link_idle: var(--blue_dark);
  --link_idle02:  var(--dark02);
  --green: #5CED6B;
  --green_mid:#48BA53;
  --green_dark:#00750C;
  --purple:#474199;
  --apuxie:#FF2B6D;
  --apuxie_bright:#F795B3;
  --purple_mid:#9E45E2/*#8854B0*/;
  --blue_mid:#12B8FF;
  --blue_dark:#004c8a;
  --internal_darks: var(--dark03);
  --line_width: 0.125em;
  --em-unitBase: 16;
  --em-unitBasePX: var(--em-unitBase) + "px";
  --body-lineHeight-min: 1.5;
  --body-lineHeight-max: 2;
  --core_tile_size: 64px;
  --border_radius: 0.15rem;
  --hero_graphics_size: 50vmin;
  /*breapoints vars*/
  --min-screen-width: 300px;
  --bp-s: 24.15; /**/
  --bp-m: 43.75; /**/
  --bp-l: 60; /*960px*/
  --bp-xl: 75; /*1200px*/
  /*Typefaces*/
  --tf-heading: 'Inter', sans-serif;
  --tf-body:   'IBM Plex Sans', sans-serif;
  /*body vars*/
  --em-unitBase: 16;
  --em-unitBasePX: 16px;
  --body-lineHeight-min: 1.3;
  --body-lineHeight-max: 1.75;
/*typo_transition*/
  --type_distance: 0.5em;
  --type_speed: 0.3s;
}
body {
  font-size:16px;
  line-height: 1.5;
}
body,
div,
p,
th,
td,
ul,
li,
h1,
h2,
h3,
h4,
h5,
h6,
blockquote,
figcaption,
cite,
em,
b,
strong,
button {

  font-style: normal;
  font-variation-settings: "wght" var(--txt-wght), "ital" var(--txt-ital);

}
strong{
  font-family: inherit;
  font-weight:600;
}

ul{
  list-style: none;
  counter-reset: item;
  margin: 0;
  padding: 0;
}

li {
  counter-increment: item;
  list-style:none;
  margin: 0;
  padding: 0;

}
ol li{
  list-style-type: none;
}
.clients_gallery{
  display:grid;
  grid-template-columns: 25% 25% 25% 25%;
  grid-template-rows: 1fr 1fr 1fr;
  height:70%;
  padding:0;
  transform:scale(0.85);
  width:100%;
  max-width:60rem;

}
.clients_gallery *{
 list-style: none;
 display:grid;
 width: 75%;
 max-width:60vw;
 justify-self:center;
 align-self:center;
 padding:4vmin;


}

.project_li a {

  font-size:inherit;
  text-decoration: none;
  border-radius:var(--border_radius);
  border: none;
  /*margin-top:1rem;
  margin-left: -0.5rem;*/
  padding:0.5rem;
  padding-left:1rem;
  padding-right:0.5rem;
  background-image: linear-gradient(-90deg, var(--dark02) 0%, var(--accent02) 50%,var(--dark_transparent) 50%, var(--dark_transparent) 100%);
  background-size:200%;
  background-position:00%;
/*  transition:all 0.2s; */
  background-repeat:no-repeat;
}
.project_li .icon_link{
  margin-top:0.5rem;
  background-image: linear-gradient(-90deg, var(--dark02) 0%, var(--accent02) 50%,transparent 50%, transparent 100%);
