/*
* Prefixed by https://autoprefixer.github.io
* PostCSS: v7.0.29,
* Autoprefixer: v9.7.6
* Browsers: last 4 version
*/


:root {
  font-size:16px;
  --dark01: #171216;
  --dark02: #473F45;
  --dark03: #888888;
  --bright01: #fafafa;
  --bright02: #eeeeee;
  --bright03:#EDEBFD;
  --bright04:#D9D8DE;
  --action: #DE6B6B;
  --accent01: var(--green_mid)/*#F97171*/ ;
  --accent02: var(--green_dark)/*#675C79*/;
  --accent03: #6BE1F1;

  --link_idle: var(--dark01);
  --link_idle02:  var(--dark02);
  --green: #5CED6B;
  --green_mid:#48BA53;
  --green_dark:#00750C;
  --purple:#46366F;
  --purple_mid:#8854B0;
  --internal_darks: var(--dark03);
  --line_width: 0.125em;
  --em-unitBase: 16;
  --em-unitBasePX: var(--em-unitBase) + "px";
  --body-lineHeight-min: 1.5;
  --body-lineHeight-max: 2;
  --core_tile_size: 64px;
  --border_radius: 0.0rem;
  /*breapoints vars*/
  --min-screen-width: 300px;
  --bp-s: 24.15; /**/
  --bp-m: 43.75; /**/
  --bp-l: 60; /*960px*/
  --bp-xl: 75; /*1200px*/
  /*Typefaces*/
  --tf-heading: 'Lato', sans-serif;
  --tf-body:  'Lato', sans-serif;
  /*body vars*/
  --em-unitBase: 16;
  --em-unitBasePX: 16px;
  --body-lineHeight-min: 1.3;
  --body-lineHeight-max: 1.75;
/*typo_transition*/
  --type_distance: 0.5em;
  --type_speed: 0.3s;
}
body {
  font-size:16px;
  line-height: 1.5;
}
body,
div,
p,
th,
td,
ul,
li,
h1,
h2,
h3,
h4,
h5,
h6,
blockquote,
figcaption,
cite,
em,
b,
strong,
button {

  font-style: normal;
  font-variation-settings: "wght" var(--txt-wght), "ital" var(--txt-ital);

}
strong{
  font-family: inherit;
  font-weight:600;
}

ul{
  list-style: none;
  counter-reset: item;
  margin: 0;
  padding: 0;
}

li {
  counter-increment: item;
  list-style:none;
  margin: 0;
  padding: 0;

}
ol li{
  list-style-type: none;
}

.project_li a {

  font-size:inherit;
  text-decoration: none;
  border-radius:var(--border_radius);
  border: none;
  /*margin-top:1rem;
  margin-left: -0.5rem;*/
  padding:0.25rem;
  padding-left:0.5rem;
  padding-right:0.5rem;
  background-image: linear-gradient(-90deg, var(--dark02) 0%, var(--accent02) 50%,transparent 50%, transparent 100%);
  background-size:200%;
  background-position:200%;
  transition:all 0s;
  background-repeat:no-repeat;
}
.project_li a:visited {
  color: var(--link_idle);
}
.project_li a:hover {
  background-position:100%;
  color: var(--accent02);

}
.project_li a:active {

}
.project_li a:focus{
  outline:none;
  background-position:100%;
  color: var(--accent02);
  /*outline: 1px solid var(--lin_idle);*/
  outline-offset:2px;

}
.name_and_title{
  position:relative;
  grid-column-start: 1 ;
  grid-column-end: 2;
  grid-row-start: 1;
  grid-row-end: 2;
  position:relative;
  z-index:2;

  width:fit-content;
  height:fit-content;
  text-align:left;
  align-self:flex-start;
  margin:0;
  margin-left: 1rem;
  margin-top: 1rem;
  color:var(--bright01);
  padding:0;
  line-height:1;
}
.name_and_title *{
  margin:0;
  padding:0;
  margin:0;


}
.body_container {
  height: 100vh;
  width: 100vw;
  display: -ms-grid;
  display: grid;
  -ms-grid-rows: 100%;
  grid-template-rows: 100%;
  -ms-grid-columns: 64px auto;
  grid-template-columns: 64px auto;
  overflow: hidden;
  margin: 0;
  padding: 0;
  background: var(--bright01);
}

.body_container > *:nth-child(1) {
  -ms-grid-row:1;
  -ms-grid-column:1;
}

.body_container > *:nth-child(2) {
  -ms-grid-row:1;
  -ms-grid-column:2;
}

.heading_01 {
  --fontsize_min: 2.665rem;
  --fontsize_max: 5.997rem;
  font-family: var(--tf-heading);
  font-size: clamp( var(--fontsize_min), 3.998vmax, var(--fontsize_max));
  font-weight: 300;
  line-height: 1.25;
  letter-spacing: 0.01em;
}
.heading_02 {
  --fontsize_min: 1.999rem;
  --fontsize_max: 3.498rem;
  font-family: var(--tf-body);
  font-size: clamp( var(--fontsize_min), 2.828vmax, var(--fontsize_max));
  font-weight: medium;
  line-height: 1;
}
.heading_03 {
  --fontsize_min: 1.302rem;
  --fontsize_max: 2.929rem;
  font-family: var(--tf-body);
  font-size: clamp( var(--fontsize_min), 1.953vmax, var(--fontsize_max));
  font-weight: 300;
  line-height: 1.25;
}
.heading_04 {
  --fontsize_min: 1.042rem;
  --fontsize_max: 2.344rem;
  font-family: var(--tf-body);
  font-size: clamp( var(--fontsize_min), 1.563vmax, var(--fontsize_max));
  font-weight: light;
  line-height: 1.5;
}
.heading_05 {
  --fontsize_min: 0.833rem;
  --fontsize_max: 1.875rem;
  font-family: var(--tf-body);
  font-size: clamp( var(--fontsize_min), 1.25vmax, var(--fontsize_max));
  font-weight: 500;
  line-height: 1.5;
  margin:0;
}
.heading_06 {
  --fontsize_min: 0.75rem;
  --fontsize_max: 1.5rem;
  font-family: var(--tf-body);
  font-size: clamp(var(--fontsize_min), 1vmax, var(--fontsize_max));
  font-weight: medium;
  line-height: 1.5;

}
.paragraph{
  margin-top:0.5rem;
  --fontsize_min: 0.875rem;
  --fontsize_max: 1.237rem;
  font-family: var(--tf-body);
  font-size: clamp(var(--fontsize_min),1vmax, var(--fontsize_max));
  font-weight: 300;
  line-height: 1.5;
  width: 60vw;
  max-width: 36rem;
  word-break: normal;

}
.paragraph *{
  font-size: inherit;
}
.paragraph a{
  outline:none;
  font-weight:bolder;
  padding:0.25rem;
  margin-left:0.15rem;
  border-radius: var(--border_radius);
  text-decoration: none;
  color: var(--dark01);
}
.paragraph a:focus{
  background-image: -webkit-gradient(linear, left top, left bottom, from(var(--accent02)), to(var(--accent01)));
  background-image: -o-linear-gradient(var(--accent02), var(--accent01));
  background-image: linear-gradient(var(--accent02), var(--accent01));
  color: var(--bright01);
  /*outline: 1px solid var(--dark01);*/
  outline-offset:2px;
}
.paragraph a:hover{
  background-image: -webkit-gradient(linear, left top, left bottom, from(var(--accent02)), to(var(--accent01)));
  background-image: -o-linear-gradient(var(--accent02), var(--accent01));
  background-image: linear-gradient(var(--accent02), var(--accent01));
  color: var(--bright01);
  /*outline: 1px solid var(--dark01);*/
  outline-offset:2px;
}
.paragraph strong{
  font-family: inherit;
  font-size: inherit;
  -webkit-hyphens: inherit;
      -ms-hyphens: inherit;
          hyphens: inherit;
  font-weight: 600;
  line-height: inherit;

}
.small {
  --fontsize_min: 0.75rem;
  --fontsize_max: 0.875rem;

  font-family: var(--tf-body);
  font-size: clamp( var(--fontsize_min), 0.75vmax, var(--fontsize_max));

}
.chips_list{

  display:flex;
  flex-direction:row;
  flex-wrap:wrap;
  gap:0.25rem;
margin-bottom:0.5rem;

}
.chip{
  display:flex;
  border: solid 1px var(--bright04);
  border-radius:1rem;

  padding:0.15rem;
  padding-left:0.5rem;
  padding-right:0.5rem;
  min-width:3rem;
  text-align:center;
  justify-content:center;
  align-content: center;
}
.service{


}
.service::before {
font-size:1rem;
margin-left:-1.8rem;
margin-right:0.25rem;
content: counter(item);
background: var(--dark01);
border-radius: 50%;
text-align: center;
display: inline-block;
color: white;
width: 1.5rem;
height:1.5rem;

}

.section_testimonials{
  padding-bottom:3rem;
}
.section_contacts{
  height:90vh;
}

.figcaption {
  font-family: var(--tf-body);
  font-size: calc(var(--h6-fontsize-min) * 1em);
  font-weight: inherit;
  line-height: 1.5;
  opacity: 0.75;
}
.section {
  width: 100%;
  margin: 0;
  margin-top:3rem;
  /* border-bottom: 2px solid var(--dark01);*/
}
.section__content {
  width: 85%;
  margin: auto;
}
.section__heading {
  width: -webkit-fit-content;
  width: -moz-fit-content;
  width: fit-content;
  background-image: -webkit-gradient(linear,left top, right top,from(#333333), color-stop(50%, var(--dark01)), color-stop(50%, transparent));
  background-image: -o-linear-gradient(left,#333333 0%, var(--dark01) 50%, transparent 50%);
  background-image: linear-gradient(90deg,#333333 0%, var(--dark01) 50%, transparent 50%);
  background-size: 200%;
  background-position: 100% 0%;
  background-repeat: repeat-x;
  color: var(--bright01);
  padding-left: 10%;
  padding-right: 0.5rem;
  padding-top: 1%;
  padding-bottom: 1%;
  display: inline-block;
  -webkit-transition: 0.33s;
  -o-transition: 0.33s;
  transition: 0.33s;
}
.section__heading--reveal {
  color: var(--bright01);
  background-position: 0% 0%;
}

.projects_list {
  -webkit-transform: none;
      -ms-transform: none;
          transform: none;
          display:flex;
          flex-direction:column;
  padding: 0;
  margin: auto;
