  color: var(--dark02);
  background-image: -webkit-gradient(linear, left top, right top, from(#d9d9d9), to(#c9c9c9));
  background-image: -o-linear-gradient(left, #d9d9d9, #c9c9c9);
  background-image: linear-gradient(90deg, #d9d9d9, #c9c9c9);
